import React from "react";
import styles from "../components/ProductCard.css";
import { useNavigate } from 'react-router-dom';
import { useCountdown } from "./useCountdown";
export const ProductCard = ({ product, seconds }) => {
    const navigate = useNavigate();
  
    const { countdown, isAlert, isDisabled } = useCountdown(seconds);
    
    return (
        <div>
            <div
            className={`${"'product-card__image'"} ${
                isDisabled ? "'product-card__item--disabled'" : ''
            }`}>
            <span
                className={`${styles['product-card__time']} ${
                isAlert ? styles['product-card__time--alert'] : ''
                } ${
                isDisabled ? styles['product-card__time--disabled'] : ''
                }`}>{`${countdown}s`}</span>
            <img src={product.image} alt={product.title} />
            </div>
            <h3 className={styles['product-card__title']}>${product.title}</h3>
            <div className={styles['product-card__footer']}>
            <p className={styles['product-card__price']}>${product.price}</p>
            <button
                type="button"
                disabled={isDisabled}
                className={`${styles['product-card__button']} ${
                isDisabled ? styles['product-card__button--disabled'] : ''
                }`}
                onClick={() => navigate(`/detalle/${product.id}`)}>
                Detalle
            </button>
            </div>
      </div>
    );
  };
