import React from "react";
import { producto } from '../../Providers/products';

const Productdetails = () => {
  const { id } = useParams();
  const [product, setProduct] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      const [data, err] = await producto({ id });
      if (err === null) {
        setProduct(data);
        setLoading(false);
      }
    };
    fetchData();
  }, [id]);

  return (
    <>
      <div className={styles['product-details__image']}>
            <img src={product.image} alt={product.title} />
          </div>
          <div>
            <div>
              <h3 className={styles['product-details__price']}>
                ${product.price}
              </h3>
              <small className={styles['product-details__rating']}>
                Rating: {product.rating.rate}
              </small>
            </div>
            <p>{product.description}</p>
            <p className={styles['product-details__category']}>
              Category: {product.category}
            </p>
          </div>
    </>
  );
};

export default Productdetails;
